mapper.select:
SELECT
rt.VALID_MONTH as validMonth,c.CARD_ID,c.CARD_NO,c.CARD_PASSWORD,c.CARD_ACTIVATE_ID,c.RECHARGE_TYPE,rt.RECHARGE_TYPE_NAME as rechargeTypeName,c.ACTIVATE_BY,c.ACTIVATE_DATE,
c.VALID_DATE,c.ORG_ID,c.SCHOOL_ID,c.STUDENT_ID,c.USER_ID,c.`STATUS`,c.CREATE_BY,c.CREATE_DATE,
c.CREATE_PLATFORM,c.CREATE_VERSION,c.UPDATE_BY,c.UPDATE_DATE,c.UPDATE_PLATFORM,c.UPDATE_VERSION,
ge.KEY_VALUE  as  STATUS_VALUE,sc.SCHOOL_NAME as schoolName , org.ORG_ABBRNAME as orgAbbrname, org.ORG_NAME as orgName FROM t_card as c
LEFT JOIN t_general AS ge ON ge.KEY_TYPE='CARD_STATUS' AND ge.KEY_CODE=c.`STATUS`
LEFT JOIN t_recharge_type AS rt on rt.RECHARGE_TYPE_ID=c.RECHARGE_TYPE
LEFT JOIN t_school as sc on sc.SCHOOL_ID=c.SCHOOL_ID
LEFT JOIN t_organization as org on org.ORG_ID=c.ORG_ID
WHERE 1=1

  AND c.ORG_ID =#{orgId}


    c.CARD_ID=#{cardId}


  and  date(c.CREATE_DATE)  >=  date(#{startDate})


  and date(c.CREATE_DATE) <= date(#{endDate})


  and c.CARD_ACTIVATE_ID=#{cardActivateId}


  and c.STATUS=#{status}


  AND c.CARD_NO like CONCAT('%',#{cardNo},'%')


  AND c.RECHARGE_TYPE =#{rechargeType}


  ORDER BY ${sortField} ${sortDir}


  ORDER BY c.CARD_ID DESC

SELECT
 IFNULL(COUNT(*),0)  AS num
 FROM t_card
   WHERE ORG_ID=#{orgId}
   AND  RECHARGE_TYPE=#{rechargeType}
   AND  STATUS=#{status}

mapper.update:
update t_card

STATUS = #{status,jdbcType=VARCHAR},
UPDATE_BY =#{updateBy},
UPDATE_DATE =#{updateDate}

WHERE STATUS ='CR'
AND CARD_ACTIVATE_ID= #{cardActivateId,jdbcType=BIGINT}

update t_card

  STATUS = #{status,jdbcType=VARCHAR},
  UPDATE_BY =#{updateBy},
  UPDATE_DATE =#{updateDate}

WHERE STATUS !='FN'
AND CARD_ID= #{cardId,jdbcType=BIGINT}
