mapper.select:
SELECT kp.KNOWLEDGE_POINT_ID, kp.ORG_ID, kp.POINT_NAME, kp.POINT_DESC, kp.MIN_DAN, kp.MAX_DAN, kp.IS_FREE, kp.POINT_TYPE, kp.STATUS, kp.CREATE_BY, kp.CREATE_DATE, kp.CREATE_PLATFORM, kp.CREATE_VERSION, kp.UPDATE_BY, kp.UPDATE_DATE, kp.UPDATE_PLATFORM, kp.UPDATE_VERSION, kp.SYSTEM_ID, ks.SYSTEM_NAME AS SYSTEM_NAME, ks2.KNOWLEDGE_SYSTEM_ID AS PARENT_SYSTEM_ID, ks2.SYSTEM_NAME AS PARENT_SYSTEM_NAME, ks.SYSTEM_LEVEL AS SYSTEM_LEVEL, ge.KEY_VALUE AS SYSTEM_LEVEL_LABEL, org.ORG_NAME AS ORG_NAME, grade.GRADE_NAME AS MIN_DAN_VALUE, grade2.GRADE_NAME AS MAX_DAN_VALUE, us.USER_NAME AS CREATE_BY_NAME, ge2.KEY_VALUE AS IS_FREE_LABEL, ge3.KEY_VALUE AS STATUS_LABEL, ge4.KEY_VALUE AS POINT_TYPE_LABEL FROM t_knowledge_point AS kp LEFT JOIN t_knowledge_system AS ks ON ks.KNOWLEDGE_SYSTEM_ID=kp.SYSTEM_ID LEFT JOIN t_general AS ge ON ge.KEY_TYPE='K_SYSTEM_LEVEL' AND ge.KEY_CODE=ks.SYSTEM_LEVEL LEFT JOIN t_organization AS org ON org.ORG_ID=kp.ORG_ID LEFT JOIN t_grade AS grade ON grade.GRADE_ID=kp.MIN_DAN LEFT JOIN t_grade AS grade2 ON grade2.GRADE_ID=kp.MAX_DAN LEFT JOIN t_user AS us ON us.USER_ID=kp.CREATE_BY LEFT JOIN t_knowledge_system AS ks2 ON ks2.KNOWLEDGE_SYSTEM_ID=ks.PARENT_SYSTEM_ID LEFT JOIN t_general AS ge2 ON ge2.KEY_TYPE='K_POINT_IS_FREE' AND ge2.KEY_CODE=kp.IS_FREE LEFT JOIN t_general AS ge3 ON ge3.KEY_TYPE='K_POINT_STATUS' AND ge3.KEY_CODE=kp.STATUS LEFT JOIN t_general AS ge4 ON ge4.KEY_TYPE='K_POINT_TYPE' AND ge4.KEY_CODE=kp.POINT_TYPE WHERE 1=1
AND kp.KNOWLEDGE_POINT_ID =#{knowledgePointId}
AND kp.POINT_NAME like CONCAT('%',#{pointName},'%')
AND kp.CREATE_PLATFORM like CONCAT('%',#{createPlatform},'%')
AND kp.ORG_ID =#{orgId}
AND kp.MIN_DAN >= #{minDan}
AND kp.MAX_DAN <= #{maxDan}
AND kp.SYSTEM_ID =#{systemId}
AND kp.POINT_TYPE=#{pointType}
AND kp.STATUS=#{status}
AND ks.SYSTEM_LEVEL=#{systemLevel}
ORDER BY ${sortField} ${sortDir}
ORDER BY kp.KNOWLEDGE_POINT_ID DESC
