mapper.select:
select FROM t_organization o LEFT JOIN t_general g ON o. STATUS = g.KEY_CODE AND g.KEY_TYPE = 'ORG_STATUS' AND g.IS_ENABLE = '1' LEFT JOIN t_user u ON o.AUDIT_BY = u.USER_ID LEFT JOIN t_user us ON o.CREATE_BY = us.USER_ID LEFT JOIN t_user usr ON o.UPDATE_BY = usr.USER_ID LEFT JOIN ( SELECT ORG_ID, IFNULL(COUNT(1), 0) AS COUNT FROM t_school GROUP BY ORG_ID ) s ON s.ORG_ID = o.ORG_ID WHERE 1=1
AND o.ORG_NAME like CONCAT('%',#{organization.orgName,jdbcType=VARCHAR},'%' )
AND o.ORG_ABBRNAME like CONCAT('%',#{organization.orgAbbrname,jdbcType=VARCHAR},'%' )
AND o.STATUS =#{organization.status,jdbcType=VARCHAR}
ORDER BY ${organization.sortField} ${organization.sortDir}
ORDER BY o.CREATE_DATE DESC

select count(1)
from t_organization
WHERE 1=1

  AND ORG_ID != #{organization.orgId,jdbcType=BIGINT}


  AND ORG_NAME =#{organization.orgName,jdbcType=VARCHAR}

select count(1)
from t_organization
WHERE 1=1

  AND ORG_ID != #{organization.orgId,jdbcType=BIGINT}


  AND ORG_ABBRNAME =#{organization.orgAbbrname,jdbcType=VARCHAR}
