mapper.select:
SELECT tp.Praxis_ID, tp.ORG_ID, tp.School_ID, tp.KB_ID, tp.Classify_ID, tp.Sub_Classify_ID, tp.DAN, tp.Praxis_Name, tp.remarks, tp.SGF, tp.Praxis_Type, tp.STATUS, tc.Classify_Name AS CLASSIFY_NAME, tc2.Classify_Name AS SUB_CLASSIFY_NAME, g.GRADE_NAME AS GRADE_NAME, sc.SCHOOL_NAME AS SCHOOL_NAME, org.ORG_NAME AS ORG_NAME, org.ORG_ABBRNAME AS ORG_ABBRNAME FROM t_temp_praxis as tp LEFT JOIN t_temp_classify AS tc ON tc.Classify_ID = tp.Classify_ID LEFT JOIN t_temp_classify AS tc2 ON tc2.Classify_ID =tp.Sub_Classify_ID LEFT JOIN t_school AS sc ON sc.SCHOOL_ID = tp.School_ID LEFT JOIN t_organization AS org ON org.ORG_ID =tp.ORG_ID LEFT JOIN t_grade AS g on g.GRADE_ID=tp.DAN WHERE 1=1
AND Praxis_Name like CONCAT('%',#{praxisName},'%')
AND Dan =#{dan}
AND tp.Classify_ID =#{classifyId}
AND tp.Sub_Classify_ID =#{subClassifyId}
ORDER BY ${sortField} ${sortDir}
ORDER BY Praxis_ID DESC

mapper.insert:

insert into t_temp_praxis (Praxis_ID, ORG_ID, School_ID, 
  KB_ID, Classify_ID, Sub_Classify_ID, 
  DAN, Praxis_Name, remarks, 
  SGF, Praxis_Type, Status
  )
values (#{praxisId,jdbcType=BIGINT}, #{orgId,jdbcType=INTEGER}, #{schoolId,jdbcType=INTEGER}, 
  #{kbId,jdbcType=INTEGER}, #{classifyId,jdbcType=INTEGER}, #{subClassifyId,jdbcType=INTEGER}, 
  #{dan,jdbcType=INTEGER}, #{praxisName,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR}, 
  #{sgf,jdbcType=VARCHAR}, #{praxisType,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}
  )
